import React, { useState } from "react";
import "./MultiStepForm.css"; // Import your CSS file with styles

function MultiStepForm() {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    input1: "",
    choices: [],
    input2: "",
    input3: "",
    input4: "",
  });
  const [emptyFields, setEmptyFields] = useState([]); // State to track empty fields

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    if (type === "checkbox") {
      // Handle checkboxes for multiple choices
      const isChecked = e.target.checked;
      setFormData((prevData) => ({
        ...prevData,
        choices: isChecked
          ? [...prevData.choices, value]
          : prevData.choices.filter((choice) => choice !== value),
      }));
    } else {
      // Handle other input fields
      setFormData((prevData) => ({
        ...prevData,
        [name]: value,
      }));
    }

    // Remove the field from emptyFields when it's no longer empty
    if (value !== "") {
      setEmptyFields((prevEmptyFields) =>
        prevEmptyFields.filter((fieldName) => fieldName !== name)
      );
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Form data submitted:", formData);
  };

  const nextStep = () => {
    if (step < 3) {
      // Check for empty fields and set the red border
      const emptyFieldsInStep = Object.keys(formData).filter(
        (fieldName) => formData[fieldName] === ""
      );
      setEmptyFields(emptyFieldsInStep);
      if (emptyFieldsInStep.length === 0) {
        setStep(step + 1);
      }
    }
  };

  const prevStep = () => {
    if (step > 1) {
      setStep(step - 1);
    }
  };

  const isStepValid = () => {
    if (step === 1) {
      return formData.input1 !== "" && formData.choices.length > 0;
    } else if (step === 2) {
      return (
        formData.input2 !== "" &&
        formData.input3 !== "" &&
        formData.input4 !== ""
      );
    }
    return true; // Step 3 has no validation
  };

  return (
    <div>
      <h1>Multi-Step Form</h1>
      <form onSubmit={handleSubmit}>
        {step === 1 && (
          <div>
            <label>Input 1:</label>
            <input
              type="text"
              name="input1"
              value={formData.input1}
              onChange={handleChange}
              className={emptyFields.includes("input1") ? "red-border" : ""}
            />
            <br />
            <label>Select options:</label>
            <div>
              <input
                type="checkbox"
                name="choices"
                value="Option 1"
                checked={formData.choices.includes("Option 1")}
                onChange={handleChange}
              />
              <label>Option 1</label>
            </div>
            {/* ... Other checkbox options ... */}
          </div>
        )}

        {step === 2 && (
          <div>
            <label>Input 2:</label>
            <input
              type="text"
              name="input2"
              value={formData.input2}
              onChange={handleChange}
              className={emptyFields.includes("input2") ? "red-border" : ""}
            />
            <br />
            <label>Input 3:</label>
            <input
              type="text"
              name="input3"
              value={formData.input3}
              onChange={handleChange}
              className={emptyFields.includes("input3") ? "red-border" : ""}
            />
            <br />
            <label>Input 4:</label>
            <input
              type="text"
              name="input4"
              value={formData.input4}
              onChange={handleChange}
              className={emptyFields.includes("input4") ? "red-border" : ""}
            />
          </div>
        )}

        {step === 3 && (
          <div>
            <p>Review your data:</p>
            <pre>{JSON.stringify(formData, null, 2)}</pre>
          </div>
        )}

        <div>
          {step > 1 && (
            <button type="button" onClick={prevStep}>
              Previous
            </button>
          )}
          {step < 3 && (
            <button type="button" onClick={nextStep} disabled={!isStepValid()}>
              Next
            </button>
          )}
          {step === 3 && (
            <button type="submit" disabled={!isStepValid()}>
              Submit
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default MultiStepForm;
