import React from "react";

function FormStep({ step, data, setData, errors, onNext }) {
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleCheckboxChange = (e) => {
    const { name, checked } = e.target;
    const updatedChoices = checked
      ? [...data.page1Choice, name]
      : data.page1Choice.filter((choice) => choice !== name);
    setData({ ...data, page1Choice: updatedChoices });
  };

  return (
    <div>
      <h2>Step {step}</h2>
      {step === 1 && (
        <div>
          <input
            type="text"
            name="page1Input"
            value={data.page1Input}
            onChange={handleInputChange}
          />
          <div>
            <label>
              <input
                type="checkbox"
                name="choice1"
                checked={data.page1Choice.includes("choice1")}
                onChange={handleCheckboxChange}
              />
              Choice 1
            </label>
            {/* Repeat for other choices */}
          </div>
        </div>
      )}
      {step === 2 && <div>{/* Render input fields for step 2 */}</div>}
      {errors.map((error, index) => (
        <p key={index} style={{ color: "red" }}>
          {error}
        </p>
      ))}
      <button onClick={onNext}>Next</button>
    </div>
  );
}

export default FormStep;
