import "./section-style.css";
import screen1 from "../../image/screen/screen-1.png";
import screen2 from "../../image/screen/screen-2.png";
import screen3 from "../../image/screen/screen-3.png";
import icon1 from "../../image/icon/icon-1.png";
import icon2 from "../../image/icon/icon-2.png";
import icon3 from "../../image/icon/icon-3.png";
import object1 from "../../image/toilet-paper.png";
import { steps, feature } from "./context";

const img = [screen1, screen2, screen3];
const icon = [icon1, icon2, icon3];
export default function Section() {
  return (
    <>
      <div className="hero-section">
        <h1 className="heading-primary">
          <span className="heading-main">Poop Prom</span>
          <span className="heading-sub">Your best toilet finder</span>
        </h1>
      </div>
      <div className="how-container intro-descript">
        <p className="step-description">
          Welcome to Poop Prom, your go-to destination for hassle-free access to
          clean and convenient public restrooms. Whether you're traveling,
          exploring the city, or just out and about, we make it easy to find the
          nearest restroom facilities. Plus, Anyone can register their toilets,
          enhancing visibility and contributing to a more accessible and
          hygienic community. Join us today for a seamless restroom experience!
        </p>
      </div>
      <section className="section-how" id="how">
        <div className="how-container">
          <span className="subheading">How it works</span>
          <h2 className="small-margin">
            Your daily dose of health in 3 simple steps
          </h2>
        </div>
        <Accordion data={steps} order={img} />
      </section>

      <section className="section-features">
        <div className="features-container center-text">
          <span className="subheading">Features</span>
          <h2 className="heading-secondary">Our Features & Services</h2>
        </div>
        <Features data={feature} order={icon} />
      </section>

      <section className="section-cta">
        <div className="container">
          <div className="cta">
            <div className="cta-text-box">
              <h2 className="heading-secondary">
                Register Your Toilet Facility Today!
              </h2>
              <p className="cta-text">
                Register your toilet facility today and join hundreds of
                businesses making a difference in our community. Help users find
                your facility easily, promote your business, and contribute to
                better access to clean restrooms.
              </p>
              <button className="cta-btn">Register Now !</button>
            </div>
            <div className="cta-img-box" aria-label="map img">
              <img className="obj-img" src={object1} alt="toilet paper" />
            </div>
          </div>
        </div>
      </section>
      <footer className="main-footer">
        <p className="copyright">
          Copyright Â© 2023 by Me, Inc. All rights reserved.
        </p>
      </footer>
    </>
  );
}
function Features({ data, order }) {
  return (
    <div className="features-container grid grid--3--cols">
      {data.map((el, i) => (
        <FeaturesItem title={el.title} text={el.text} order={order[i]} />
      ))}
    </div>
  );
}

function FeaturesItem({ title, text, order }) {
  return (
    <div className="features-box">
      <img src={order} className="icon-img" alt="iCon" />
      <h3 className="features-heading heading-color">{title}</h3>
      <p className="features-description">{text}</p>
    </div>
  );
}

function Accordion({ data, order }) {
  return (
    <div className="how-container grid grid--2--cols grid--center--v">
      {data.map((el, i) => (
        <>
          {i % 2 === 0 ? ( // Check if i is even
            <>
              <AccordionItem
                title={el.title}
                text={el.text}
                num={i}
                key={el.title}
              />
              <StepImage order={order[i]} />
            </>
          ) : (
            // i is odd
            <>
              <StepImage order={order[i]} />
              <AccordionItem
                title={el.title}
                text={el.text}
                num={i}
                key={el.title}
              />
            </>
          )}
        </>
      ))}
    </div>
  );
}

function AccordionItem({ num, title, text }) {
  return (
    <div className="step-text-box">
      <p className="step-number">{num < 9 ? `0${num + 1}` : num + 1}</p>
      <h3 className="heading-tertiary">{title}</h3>
      <p className="step-description">{text}</p>
    </div>
  );
}

function StepImage({ order }) {
  console.log(order);
  return (
    <div className="step-img-box">
      <img
        src={order}
        className="step-img"
        alt="iPhone app preferences selection screen"
      />
    </div>
  );
}
