import React, { useState } from "react";

function MultiStepForm() {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    page1Input: "",
    page1Choice: [],
    page2Inputs: ["", "", "", ""],
  });
  const [errors, setErrors] = useState([]);

  // Validate if all fields are filled before moving to the next step
  const isStepValid = () => {
    // Implement validation logic here
    return true; // Change this based on your validation criteria
  };

  const handleNext = () => {
    if (isStepValid()) {
      setCurrentStep(currentStep + 1);
    } else {
      // Handle validation errors, e.g., show red borders on empty fields
      setErrors(["Please fill in all required fields"]);
    }
  };

  const handleSubmit = () => {
    console.log(formData); // Log the form data
  };

  return (
    <div className="App">
      {currentStep === 1 && (
        <FormStep
          step={1}
          data={formData}
          setData={setFormData}
          errors={errors}
          onNext={handleNext}
        />
      )}
      {currentStep === 2 && (
        <FormStep
          step={2}
          data={formData}
          setData={setFormData}
          errors={errors}
          onNext={handleNext}
        />
      )}
      {currentStep === 3 && (
        <div>
          <p>Review your data:</p>
          <pre>{JSON.stringify(formData, null, 2)}</pre>
          <button onClick={handleSubmit}>Submit</button>
        </div>
      )}
    </div>
  );
}

export default MultiStepForm;
