import React from "react";
import { useState, useEffect } from "react";

export default function ToiletInfo({
  formData,
  setFormData,
  choose,
  setChoose,
}) {
  const [file, setFile] = useState(null);

  useEffect(() => {
    const storedFileURL = localStorage.getItem("fileURL");
    if (storedFileURL) {
      setFile(storedFileURL);
    }
  }, []);

  function handleChange(e) {
    const selectedFile = e.target.files[0];
    const fileURL = URL.createObjectURL(selectedFile);
    localStorage.setItem("fileURL", fileURL);
    setFile(fileURL);
    setFormData({ ...formData, photo: selectedFile.name });
  }

  function handleToggleItems(id) {
    setChoose((choose) =>
      choose.map((item) =>
        item.id === id ? { ...item, status: !item.status } : item
      )
    );
  }

  return (
    <div className="Toilet-info-container">
      <div>
        <label>Address</label>
        <input
          type="text"
          placeholder="Address"
          value={formData.address}
          onChange={getHandler("address")}
          required
        />
      </div>

      <div>
        <label>District / County</label>
        <input
          type="text"
          placeholder="District / County"
          value={formData.district}
          onChange={getHandler("district")}
          required
        />
      </div>
      <div>
        <label>Province</label>
        <input
          type="text"
          placeholder="Province"
          value={formData.province}
          onChange={getHandler("province")}
          required
        />
      </div>
      <div>
        <label>Zip code</label>
        <input
          type="text"
          placeholder="Zip code"
          value={formData.zipCode}
          onChange={getHandler("zipCode")}
          required
        />
      </div>
      <button className="map-btn">
        <UilMapMarkerAlt size="24" color="#62C2C0" />
        <span className="btn-text">Choose from the map</span>
        <UilAngleRight size="30" color="#62C2C0" className="end" />
      </button>
    </div>
  );
}
