import React, { Component } from "react";
import Step1 from "./Step1";
import Step2 from "./Step2";
import ToiletInfo from "./ToiletInfo";
import SubmitInfo from "./SubmitInfo";
import ToiletLocation from "./ToiletLocation";
import "./form-style.css";

class MultiStepForm extends Component {
  constructor(props) {
    super(props);
    this.state = {
      step: 1,
      formData: {
        toiletName: "",
        photo: "",
        address: "",
        district: "",
        province: "",
        zipCode: "",
      },
      errors: {},
    };
  }

  // Function to handle form submission for each step
  handleSubmit = (event) => {
    event.preventDefault();
    const { step, formData } = this.state;

    // Validate the current step's fields
    if (this.validateStep(step)) {
      // If the current step is the last step, you can submit the form data
      if (step === 3) {
        // Handle form submission or API request here
        console.log("Form submitted:", formData);
      } else {
        // Proceed to the next step
        this.setState({ step: step + 1 });
      }
    }
  };

  // Function to validate the fields for the current step
  validateStep = (step) => {
    const { formData } = this.state;
    let errors = {};

    // Add validation logic here for each step
    if (step === 1) {
      if (!formData.toiletName) {
        errors = {
          ...errors,
          toiletName: "Field is required",
        };
      }
    } else if (step === 2) {
      if (!formData.step2Field1 || !formData.step2Field2) {
        errors = {
          ...errors,
          address: "Field is required",
          district: "Field is required",
          province: "Field is required",
          zipCode: "Field is required",
        };
      }
    }

    // Update the state with validation errors (if any)
    this.setState({ errors });

    // Return true if there are no validation errors
    return Object.keys(errors).length === 0;
  };

  // Function to update form data when input fields change
  handleInputChange = (event) => {
    const { name, value } = event.target;
    const { formData } = this.state;
    this.setState({ formData: { ...formData, [name]: value } });
  };

  // Function to go to the previous step
  goToPreviousStep = () => {
    const { step } = this.state;
    if (step > 1) {
      this.setState({ step: step - 1 });
    }
  };

  render() {
    const { step, formData, errors } = this.state;

    return (
      <div className="multistep-form">
        <h1>Multi-Step Form</h1>
        <form onSubmit={this.handleSubmit}>
          {step === 1 && (
            <Step1
              formData={formData}
              errors={errors}
              handleChange={this.handleInputChange}
            />
          )}
          {step === 2 && (
            <Step2
              formData={formData}
              errors={errors}
              handleChange={this.handleInputChange}
            />
          )}
          {step === 3 && <SubmitInfo />}
          <div className="form-navigation">
            {step > 1 && (
              <button type="button" onClick={this.goToPreviousStep}>
                Previous
              </button>
            )}
            {step < 3 && <button type="submit">Next</button>}
            {step === 3 && <button type="submit">Submit</button>}
          </div>
        </form>
      </div>
    );
  }
}

export default MultiStepForm;
