import React from "react";
import { useState, useEffect } from "react";

export default function ToiletLocation({ formData, setFormData }) {
  const getHandler = (name) => {
    return (event) => {
      setFormData({ ...formData, [name]: event.target.value });
    };
  };

  function Input({ name, onChange }) {
    return (
      <input placeholder={name} value={formData.name} onChange={onChange} />
    );
  }

  return (
    <div className="Toilet-info-container">
      <div>
        <label>Address</label>
        <input
          type="text"
          placeholder="Address"
          value={formData.address}
          onChange={getHandler("address")}
          required
        />
      </div>
      <div>
        <label>District / County</label>
        <input
          type="text"
          placeholder="District / County"
          value={formData.district}
          onChange={getHandler("district")}
          required
        />
      </div>
      <div>
        <Input name={"province"} onChange={getHandler("province")} />
      </div>
    </div>
  );
}
